function crunchify_social_sharing_buttons($content) {
  global $post;
  global $wp;
  //if(is_singular() || is_home()){
  
    // Get current page URL 
    $crunchifyURL = ("https://$_SERVER[HTTP_HOST]$_SERVER[REQUEST_URI]");
 
